import React from 'react';
import './Banner.css';

class Banner extends Component {
	constructor() {
		super();
		this.state = {
			classNames: 'banner'
		};
	}
	
	fadeOut() {
		this.setState({classNames: 'banner fade-out'});
		props.trigger();
	}
	
	componentDidMount() {
		window.setTimeout(this.fadeOut, 1000);		
	}
	
	render() {
		const message = 'Ramble on!';
		return (
			<div className={this.state.classNames}>
				<p>{message}</p>
			</div>
		);
	}
}

export default Banner;
