import React from 'react';
import './Banner.css';

class Banner extends Component {
	fadeOut() {
		
		props.trigger();
	}
	
	componentDidMount() {
		window.setTimeout(this.fadeOut, 1000);		
	}
	
	render() {
		const message = 'Ramble on!';
		return (
			<div className="banner">
				<p>{message}</p>
			</div>
		);
	}
}

export default Banner;
