import React, { Component } from 'react';
import Nav from './Nav';
import Banner from './Banner';
import Button from './Button';
import PlayerContainer from './PlayerContainer';
import './App.css';

class App extends Component {
	constructor() {
		super();
		this.state = {
			showMode: 'button',
			isPlayerCollapsed: false
		};
	}

	handleClick() {
		let showMode;
		if (this.state.showMode === 'button') showMode = 'banner';
		if (this.state.showMode === 'banner') showMode = 'player';
		this.setState({showMode});
	}

	render() {
		const display = this.state.showMode === 'button' ? <Button/> : this.state.showMode === 'banner' ? <Banner/> : <PlayerContainer/>;
		return (
			<div className="App">
				<Nav/>
				{display}
			</div>
		);
	}
}

export default App;
